package assignment5ForLoop;

public class Question10NestedLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
       int column=7;
		
		
		for(int i=1;i<=column;i++) {
			
			for(int j=1;j<=i;j++) {
				
				System.out.print(i);
			
			}
			System.out.println(" ");
			
		}
	}

}
