package day11ForLoop;

public class Task49 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

         int rows=1;
		
		
		for(int i=4;i>=rows;i--) {
			
			for(int j=4;j>=i;j--) {
				
				System.out.print( " ");
			
			}
			
			System.out.println(i);
			
			}
	}

}
